import React, { useState, useEffect } from 'react'
import Notification from './components/Notification'
import Filter from "./components/Filter";
import PersonForm from "./components/PersonForm";
import Persons from "./components/Persons";
import personService from './services/persons'


const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setFilter] = useState('')
  const [message, setMessage] = useState(null)

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
    })
  }, [])




  const addPerson = (event) => {
    event.preventDefault()
    const newPerson = { name: newName, number: newNumber}

    setNewName('')
    setNewNumber('')

    const alreadyExisted = persons.some(person => person.name === newName)
    if (alreadyExisted) {
      const oldPerson = persons.find(person => person.name === newName)
      const updatedPerson = { ...oldPerson, number: newNumber}
      const confirmUpdate = window.confirm(
        `${newName} is already added to phonebook, replace the old number with a new one?`
      )
      if (confirmUpdate) {
        personService
          .update(updatedPerson.id, updatedPerson)
          .then(returnedPerson => {
            setPersons(persons.map(p => p.id === returnedPerson.id ? returnedPerson : p))
            notify(`Updated number for ${returnedPerson.name}`, "notification")
          })
          .catch(err => notify(err.response.data.error, "error"))
      }
    } else {
      personService
        .create(newPerson)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          notify(`Added ${returnedPerson.name}`, "notification")
        })
        .catch(err => notify(err.response.data.error, "error"))
    }
  }


  const handleDelete = (id) => {
    const personToDel = persons.find(person => person.id === id)
    if (window.confirm(`Delete ${personToDel.name}?`)) {
      personService
        .remove(id)
        .then(() => {
          setPersons(persons.filter(person => person.id !== id))
          setMessage({ notification: `Deleted ${personToDel.name}` })
          setTimeout(() => setMessage(null), 3000)
        })
        .catch(err => notify(err.response.data.error, "error"))
    }
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilter = (event) => {
    const search = event.target.value
    setFilter(search.toLowerCase())
  }

  const notify = (message, type) => {
    setMessage({ [type]: message })
    setTimeout(() => setMessage(null), 3000)
  }



  const handleNameClick = () => {
    const updatedName = newName.toLowerCase()
    const updatedNumber = newNumber

    if (updatedName === '' || updatedNumber === '') {
      alert(`pleaese enter a name and number`)
    } else {
        const updatedPersons = {...persons ,[updatedName]: updatedNumber}
        setPersons(updatedPersons)
    }
  }






  return (
    <div>
      <h2>Phonebook</h2>
      <Filter persons={persons}
              newFilter={newFilter}
              addPerson={addPerson}
              handleFilter={handleFilter} />
      <h2>add a new</h2>
      <PersonForm addPerson = {addPerson}
                  newName = {newName}
                  newNumber = {newNumber}
                  handleNewName = {handleNewName}
                  handleNewNumber = {handleNewNumber} />
      <h2>Numbers</h2>
      <Persons persons={persons} handleDelete={handleDelete}/>
      ...
    </div>
  )
}

export default App
