import { useState } from 'react'


const Button = ({Click, text}) => (
  <button onClick={Click}>
    {text}
  </button>
)

const DisplayName = ({persons}) => {
  const nameList = Object.keys(persons)
  return (
    <div>
      {nameList.map((name) => (
        <div>
          {name}
        </div>
      ))}
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState({})
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
  }


  const handleChange = (event) => {
    console.log(event.target.values);
    const {name, number} = event.target.value

    setNewName(name)
    setNewNumber(number)

  }


  const handleNameClick = () => {
    const updatedName = newName
    const updatedNumber = newNumber

    if (updatedName in persons){
      alert(`${updatedName} is already added to the phonebook`)
    }
    if (!(updatedName in persons)){
      const updatedPersons = {...persons, [updatedName] : [updatedNumber]}
      setPersons(updatedPersons)
      console.log(updatedPersons)
    }
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name:{" "}
          <input name="name" value={newName} onChange={handleChange}/>
        </div>
        <div>
          number:{" "}
          <input name="number" value={newNumber} onChange={handleChange} />
        </div>
        <div>
          <Button Click={handleNameClick} text='add'/>
        </div>
      </form>
      <h2>Numbers</h2>
      <DisplayName persons={persons}/>
      ...
    </div>
  )
}

export default App
